//SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import "./ILeafWalletRegistryActivate.sol";
import "./LeafWalletRegistryCore.sol";
import {LeafWalletStorage,StorageHandler} from  "./SusuwatariStorage.sol";

contract LeafWalletRegistryActivate is ILeafWalletRegistryActivate, LeafWalletRegistryCore  {
    event AssignActivatableAddressToSenderReturn(uint256);
    

    function assignActivatableAddressToSender(address assignee) public virtual override{
        LeafWalletStorage storage lw = lw();
        _assignAddressToSender(assignee);


        uint256 activationCode = uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender))) % 100;
        if(activationCode<10){
            activationCode = activationCode+10;
        }
        lw._deviceActivationCodes[assignee] = activationCode;


        emit AssignActivatableAddressToSenderReturn(activationCode);
    }

    function getActivationCodeOfSender() public view override returns (uint256){
        LeafWalletStorage storage lw = lw();
        require(lw._deviceOwner[msg.sender] != address(0),"Device not yet registered");
        return lw._deviceActivationCodes[msg.sender];
    }


}

contracts/LeafWalletRegistryCore.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import "./ILeafWalletRegistryCore.sol";
import {LeafWalletStorage,StorageHandler} from  "./SusuwatariStorage.sol";

contract LeafWalletRegistryCore is ILeafWalletRegistryCore,StorageHandler {

    

    function _assignAddressToSender(address assignee) internal{
        LeafWalletStorage storage lw = lw();
        require(lw._deviceOwner[assignee] == address(0) || lw._deviceOwner[assignee] == msg.sender,"Device already registered");
     
        if(lw._deviceOwner[assignee] == address(0)){
            lw._ownerDevices[msg.sender].push(assignee);
            lw._deviceOwner[assignee] = msg.sender;
        }
    }


    function isSenderRegistered() public view override returns (bool) {
        LeafWalletStorage storage lw = lw();
        return lw._deviceOwner[msg.sender] != address(0);
    }

    function getDeviceOwner() public view override returns (address) {
        LeafWalletStorage storage lw = lw();
        return lw._deviceOwner[msg.sender];
    }

    function getRelatedDevices() public view override returns (address[] memory) {
        LeafWalletStorage storage lw = lw();
        return lw._ownerDevices[msg.sender];
    }


    


}

contracts/LibSusuwatari.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import {SusuwatariStorage, Susu, UserState, BaggageSlot} from "./SusuwatariStorage.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";

import "hardhat/console.sol";

library LibSusuwatari {

    struct SusuwatariInfo {
        uint256 tokenId;
        address owner;
        bool isCarrying;

    }

    struct BaggedSusuInfo {
        uint256 tokenId;
        address carrier;
    }

    modifier mustExistSusu(SusuwatariStorage storage sus, uint256 tokenId) {
        require(
            tokenId > 0 && tokenId <= sus.susuOwners.length,
            "The Susuwatari does not exist"
        );
        _;
    }

    modifier mustHaveSusu(SusuwatariStorage storage sus) {
        bool hasSusu = false;

        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            if (sus.susuOwners[i] == msg.sender) {
                hasSusu = true;
                break;
            }
        }

        require(hasSusu, "Caller does not own any Susuwatari token");
        _;
    }

    modifier mustCarrySusu(SusuwatariStorage storage sus, uint256 tokenId) {
        Susu storage susuInstance = sus.tokenIdToSusu[tokenId];
        require(
            susuInstance.carrier == msg.sender,
            "Caller doesn't carry a Susuwatari Token!"
        );
        _;
    }

    modifier isUserRegistered(SusuwatariStorage storage sus) {
        require(sus.maxSlotCount[msg.sender] > 0, "User is not registered");
        _;
    }

    modifier mustNotCarrySusu(SusuwatariStorage storage sus) {
        bool isCarrying = false;

        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            if (sus.tokenIdToSusu[i + 1].carrier == msg.sender) {
                isCarrying = true;
                break;
            }
        }
        require(
            isCarrying == false,
            "Caller is already carrying a Susuwatari Token!"
        );
        _;
    }

    modifier isNotBeingCarriedSusu(
        SusuwatariStorage storage sus,
        uint256 tokenId
    ) {
        Susu storage su = sus.tokenIdToSusu[tokenId];
        require(su.carrier == address(0), "Susu is already being carried!");
        _;
    }

    modifier cannotBeOwner(SusuwatariStorage storage sus, uint256 tokenId) {
        require(
            sus.susuOwners[tokenId - 1] != msg.sender,
            "The owner cannot move his own aimed Susuwatari!"
        );
        _;
    }

    modifier mustHaveBagSpace(SusuwatariStorage storage sus) {
        bool isCarrying = false;

        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            if (sus.tokenIdToSusu[i + 1].carrier == msg.sender) {
                isCarrying = true;
                break;
            }
        }
        require(isCarrying == false, "Caller is already overburdened");
        _;
    }

    modifier susuMustNotHaveBeenAimed(
        SusuwatariStorage storage sus,
        uint256 tokenId
    ) {
        require(
            sus.susuOwners[tokenId - 1] == msg.sender &&
                sus.tokenIdToSusu[tokenId].carrier == msg.sender,
            "Caller is not owner"
        );

        _;
    }

    function getCurrentState(
        SusuwatariStorage storage sus
    ) internal view returns (UserState memory) {
        uint256 ownedTokenCount = 0;
        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            if (msg.sender == sus.susuOwners[i]) {
                ownedTokenCount++;
            }
        }

        uint256[] memory ownedTokens = new uint256[](ownedTokenCount); //push geht nicht in memory arrays
        uint256 index = 0;
        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            if (msg.sender == sus.susuOwners[i]) {
                ownedTokens[index] = i + 1;
                index++;
            }
        }

        BaggageSlot memory slot;
        for (uint256 i = 0; i < sus.susuOwners.length; i++) {
            uint256 tokenId = i + 1;

            if (sus.baggedSusus[tokenId] == msg.sender) {
                slot.susuTokenId = tokenId;
                slot.dropCooldownTime = sus
                    .tokenIdToSusu[tokenId]
                    .dropCooldownTime;
                slot.ownerAddress = sus.susuOwners[tokenId - 1];
                break; //one slot per user!
            }
        }

        UserState memory state = UserState({
            ownedTokens: ownedTokens,
            slot: slot,
            team: sus.ownerToTeam[msg.sender]
        });

        return state;
    }

function registerMe(SusuwatariStorage storage sus, uint8 team) internal 
returns (uint256, string memory, address,uint8)
{
    require(sus.maxSlotCount[msg.sender] == 0, "User already registered");

    Susu memory newSusu = Susu({
        tokenId: sus.susuOwners.length + 1,
        dropCooldownTime: 0,
        originLocation: 0,
        currentLocation: 0,
        destination: 0,
        message: "",
        carrier: msg.sender
    });

    sus.maxSlotCount[msg.sender] = 1;
    sus.ownerToTeam[msg.sender] = team;
    sus.susuOwners.push(msg.sender);
    sus.tokenIdToSusu[newSusu.tokenId] = newSusu;
    sus.baggedSusus[newSusu.tokenId] = msg.sender;


        return (newSusu.tokenId, newSusu.message, newSusu.carrier, team);
}
    function giveSusuwatari(
        SusuwatariStorage storage sus
    ) internal isUserRegistered(sus) mustNotCarrySusu(sus) 
    returns (uint256, string memory, address, uint8){
        Susu memory newSusu = Susu({
            tokenId: sus.susuOwners.length + 1,
            dropCooldownTime: 0,
            originLocation: 0,
            currentLocation: 0,
            destination: 0,
            message: "",
            carrier: msg.sender
        });

        sus.susuOwners.push(msg.sender);
        sus.tokenIdToSusu[newSusu.tokenId] = newSusu;
        sus.baggedSusus[newSusu.tokenId] = msg.sender;

    uint8 team = sus.ownerToTeam[msg.sender];
        
        return (newSusu.tokenId, newSusu.message, msg.sender, team);
    }

    function aimInitialSusu(
    SusuwatariStorage storage sus,
    uint256 tokenId,
    uint256 location,
    uint256 destination,
    string memory message
)
    internal
    isUserRegistered(sus)
    mustExistSusu(sus, tokenId)
    susuMustNotHaveBeenAimed(sus, tokenId)
    returns (uint256, uint256, uint256, string memory, uint8)
{
    Susu storage susu = sus.tokenIdToSusu[tokenId];

    susu.dropCooldownTime =
        300 +
        (uint256(
            keccak256(
                abi.encodePacked(block.timestamp, msg.sender, tokenId)
            )
        ) % 301);
    susu.originLocation = location;
    susu.currentLocation = location;
    susu.destination = destination;
    susu.message = message;
    susu.carrier = address(0);
    sus.baggedSusus[tokenId] = address(0);
    sus.tokenIdToSusu[tokenId] = susu;
    
    uint8 team = sus.ownerToTeam[msg.sender];
    return (tokenId, location, destination, message, team);
}


    function dropSusu(
        SusuwatariStorage storage sus,
        uint256 tokenId,
        uint256 location
    )
        internal
        isUserRegistered(sus)
        mustExistSusu(sus, tokenId)
        mustCarrySusu(sus, tokenId)
        returns (uint256, uint256, uint256, uint256, uint8, string memory)
    {
        Susu storage susu = sus.tokenIdToSusu[tokenId];

        susu.dropCooldownTime = 0;
        susu.currentLocation = location;
        susu.carrier = address(0);
        sus.baggedSusus[tokenId] = address(0);
        sus.tokenIdToSusu[tokenId] = susu;

       


        return (susu.originLocation, susu.currentLocation, susu.destination, tokenId,sus.ownerToTeam[msg.sender], susu.message);
   
    }

function tryPickupSusu(
    SusuwatariStorage storage sus,
    uint256 tokenId, //auto-ermitteln
    uint256 location
)
    internal
    isUserRegistered(sus)
    mustExistSusu(sus, tokenId)
    mustNotCarrySusu(sus)
    isNotBeingCarriedSusu(sus, tokenId)
    cannotBeOwner(sus, tokenId)
    returns (uint256, uint256, string memory, address, address, uint8)
{
    Susu storage susu = sus.tokenIdToSusu[tokenId];

    // require(
    //     susu.currentLocation == location,
    //     "Caller is not in the correct location"
    // );

    susu.dropCooldownTime =
        300 +
        (uint256(
            keccak256(
                abi.encodePacked(block.timestamp, msg.sender, tokenId)
            )
        ) % 301);
    susu.carrier = msg.sender;
    sus.baggedSusus[tokenId] = msg.sender;
    sus.tokenIdToSusu[tokenId] = susu;

    address susOwner = sus.susuOwners[tokenId-1];
    uint8 team = sus.ownerToTeam[susOwner];


        return (tokenId, location, susu.message, msg.sender, susOwner, team);
   
}

    function getAllSusuwataris(
        SusuwatariStorage storage sus
    ) internal view returns (SusuwatariInfo[] memory) {
        uint256 length = sus.susuOwners.length;
        SusuwatariInfo[] memory susuwataris = new SusuwatariInfo[](length);
        
        for (uint256 i = 0; i < length; i++) {
            uint256 currTokenId = i+1;
            bool isBeingCarried = sus.baggedSusus[currTokenId] != address(0);
            susuwataris[i] = SusuwatariInfo({
                tokenId: currTokenId,
                owner: sus.susuOwners[i],
                isCarrying: isBeingCarried
            });
        }
        
        return susuwataris;
    }

    function getBaggedSusus(
        SusuwatariStorage storage sus
    ) internal view returns (BaggedSusuInfo[] memory) {
        uint256 length = sus.susuOwners.length;
        uint256 baggedCount = 0;

        for (uint256 i = 0; i < length; i++) {
            if (sus.baggedSusus[i + 1] != address(0)) {
                baggedCount++;
            }
        }

        BaggedSusuInfo[] memory baggedSusus = new BaggedSusuInfo[](baggedCount);
        uint256 index = 0;

        for (uint256 i = 0; i < length; i++) {
            uint256 tokenId = i + 1;
            if (sus.baggedSusus[tokenId] != address(0)) {
                baggedSusus[index] = BaggedSusuInfo({
                    tokenId: tokenId,
                    carrier: sus.baggedSusus[tokenId]
                });
                index++;
            }
        }

        return baggedSusus;
    }

}

contracts/SusuwatariFacet.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {SusuwatariStorage, StorageHandler, UserState, BaggageSlot, Susu} from "./SusuwatariStorage.sol";
import {LibSusuwatari} from "./LibSusuwatari.sol";
import {UsingDiamondOwner} from "hardhat-deploy/solc_0.8/diamond/UsingDiamondOwner.sol";

/**
 * @dev Implementation of https://eips.ethereum.org/EIPS/eip-721[ERC721] Non-Fungible Token Standard, including
 * the Metadata extension, but not including the Enumerable extension, which is available separately as
 * {ERC721Enumerable}.
 */

contract SusuwatariFacet is StorageHandler, UsingDiamondOwner {
    using Address for address;
    using Strings for uint256;


    event DroppedSusu(
        uint256 originLocation,
        uint256 currentLocation,
        uint256 destination,
        uint256 tokenId,
        uint8 team,
        string message
    );

    
event MintedSusu(uint256 tokenId, string message, address owner, uint8 team);

event PickedUpSusu(uint256 tokenId, uint256 location, string message, address sender, address owner, uint8 team);

    
    function registerMe(uint8 team) external {
        (uint256 tokenId, string memory message, address owner,uint8 setteam) = LibSusuwatari.registerMe(susu(), team);
        
        emit MintedSusu(tokenId, message, owner, setteam);
    }

    
function aimInitialSusu(
    uint256 tokenId,
    uint256 location,
    uint256 destination,
    string memory message
) external {
    
    (
        uint256 rettokenId, 
        uint256 retlocation, 
        uint256 retdestination, 
        string memory retmessage, 
        uint8 team
    ) = LibSusuwatari.aimInitialSusu(
        susu(),
        tokenId,
        location,
        destination,
        message
    );
        
    emit DroppedSusu(retlocation, retlocation, retdestination, rettokenId, team, retmessage);
     
}

  function dropSusu(uint256 tokenId, uint256 location) external {
        (uint256 originLocation, uint256 currentLocation, uint256 destination, uint256 rettokenId, uint8 team, string memory message) = LibSusuwatari.dropSusu(susu(), tokenId, location);
        
        emit DroppedSusu(originLocation, currentLocation, destination, rettokenId, team, message);
    }
    
  
    function tryPickupSusu(uint256 tokenId, uint256 location) external {
        (uint256 rettokenId, uint256 retlocation, string memory message, address sender, address owner, uint8 team) = LibSusuwatari.tryPickupSusu(susu(), tokenId, location);
     
        emit PickedUpSusu(rettokenId, retlocation, message, sender, owner, team);
    }

    function getCurrentState() external view returns (UserState memory) {
        return LibSusuwatari.getCurrentState(susu());
    }

    function giveSusuwatari() external {
        (uint256 tokenId, string memory message, address owner, uint8 team) = LibSusuwatari.giveSusuwatari(susu());
        
        emit MintedSusu(tokenId, message, owner, team);
    }

    function getAllSusuwataris()
        external
        view
        returns (LibSusuwatari.SusuwatariInfo[] memory)
    {
        return LibSusuwatari.getAllSusuwataris(susu());
    }

    function getBaggedSusus()
        external
        view
        returns (LibSusuwatari.BaggedSusuInfo[] memory)
    {
        return LibSusuwatari.getBaggedSusus(susu());
    }
}

